import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';

export enum toastTypes {
  error,
  info,
  success,
  warning
};

export interface ToastData {
  message: string;
  type: toastTypes;
  bgColor: string;
  textColor: string;
  show?: boolean;
}

@Injectable({
  providedIn: 'root'
})

export class ToastService {
  data!: ToastData;
  public open = new Subject<ToastData>();

  openToast(data: ToastData) {
    this.data = { ...data, show: true};
    document.getElementById('toast-alert')!.classList.toggle('-translate-x-4');

    setTimeout(() => {
      document.getElementById('toast-alert')!.classList.toggle('-translate-x-4');
    }, 1000);
  }

  closeToast(element: Element) {
    element.classList.toggle('scale-0');
    this.data = { ...this.data, show: false };
  }
}
