import { animate, style, transition, trigger, state } from '@angular/animations';
import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { ToastService } from 'src/app/services/toast.service';

@Component({
  selector: 'app-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.scss'],
  animations: [
    trigger('openClose', [
      state(
        'closed',
        style({
          visibility: 'hidden',
          right: '-400px',
        })
      ),
      state(
        'open',
        style({
          right: '40px',
        })
      ),
      transition('open <=> closed', [animate('0.5s ease-in-out')]),
    ]),
  ],
})

export class ToastComponent implements OnInit {
  constructor(public toastService: ToastService) {}

  @ViewChild('element', {static: false}) progressBar!: ElementRef;
  progressInterval: any;

/*   bgColor: string = 'bg-green-200';
  textColor: string = 'text-green-500';
  typeMessage: string = 'success';
  message: string = 'no message';

  public openToast(bgColor: string, textColor: string, typeMessage: string, message: string): void {
    this.bgColor = bgColor;
    this.textColor = textColor;
    this.typeMessage = typeMessage;
    this.message = message;
    document.getElementById('toast-alert')!.classList.toggle('scale-0');
    setTimeout(() => {
      document.getElementById('toast-alert')!.classList.toggle('scale-0');
    }, 5000);
  }

  closeToast(element: Element): void {
    element.classList.toggle('scale-0');
  } */

  ngOnInit(): void {
    this.toastService.open.subscribe(data => {
      if (data.show) { this.countDown() };
    });
  }

  countDown() {
    this.progressBar.nativeElement.style.width = this.toastService.data.progressWidth;
    this.progressInterval = setInterval(() => {
      const width = parseInt(this.progressBar.nativeElement.style.width, 10);

      if(width <= 0) {
        this.toastService.hide();
        clearInterval(this.progressInterval);
        return;
      }

      this.toastService.data.progressWidth = String(width - 2);
      this.progressBar.nativeElement.style.width = this.toastService.data.progressWidth + '%';
    }, 150);
  }

  stopCountDown() {
    clearInterval(this.progressInterval);
  }
}
